import React, { useState } from "react";
import WalletConnect from "@walletconnect/client";
import QRCode from "qrcode.react";

const App = () => {
  const [walletConnected, setWalletConnected] = useState(false);
  const [account, setAccount] = useState(null);
  const [walletConnector, setWalletConnector] = useState(null);

  // Función para conectar la billetera
  const connectWallet = async () => {
    // Crear un nuevo conector de WalletConnect
    const connector = new WalletConnect({
      bridge: "https://bridge.walletconnect.org", // Puente WalletConnect estándar
    });

    // Verificar si ya existe una sesión activa
    if (!connector.connected) {
      // Crear una nueva sesión (esto genera un código QR)
      await connector.createSession();
    }

    // Escuchar eventos de conexión
    connector.on("connect", (error, payload) => {
      if (error) {
        console.error("Error al conectar:", error);
        return;
      }

      // Obtener cuenta conectada
      const { accounts } = payload.params[0];
      setAccount(accounts[0]); // Guardar la cuenta conectada
      setWalletConnected(true); // Cambiar estado a conectado
    });

    // Escuchar eventos de desconexión
    connector.on("disconnect", (error, payload) => {
      if (error) {
        console.error("Error al desconectar:", error);
        return;
      }

      setWalletConnected(false); // Cambiar estado a desconectado
      setAccount(null); // Limpiar cuenta
      setWalletConnector(null); // Limpiar conector
    });

    // Guardar el conector en el estado
    setWalletConnector(connector);
  };

  // Función para desconectar la billetera
  const disconnectWallet = () => {
    if (walletConnector) {
      walletConnector.killSession(); // Terminar sesión activa
    }
  };

  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h1>Xolos $RMZ DAO</h1>
      {!walletConnected ? (
        <div>
          <button
            onClick={connectWallet}
            style={{
              padding: "10px 20px",
              fontSize: "16px",
              backgroundColor: "#4CAF50",
              color: "white",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            Conectar Billetera
          </button>
          {walletConnector && (
            <div style={{ marginTop: "20px" }}>
              <p>Escanea este código QR con tu billetera Paytaca:</p>
              <QRCode value={walletConnector.uri} size={200} />
            </div>
          )}
        </div>
      ) : (
        <div>
          <p>
            <strong>Billetera conectada:</strong> {account}
          </p>
          <button
            onClick={disconnectWallet}
            style={{
              padding: "10px 20px",
              fontSize: "16px",
              backgroundColor: "#f44336",
              color: "white",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            Desconectar Billetera
          </button>
        </div>
      )}
    </div>
  );
};

export default App;
